CREATE VIEW CheckingOutToday (guestNo, guestName, guestAddress, roomNo, numDays, totalCost) AS SELECT DISTINCT g.guestNo, g.guestName, g.guestAddress, b.roomNo, DATEDIFF(b.dateTo, b.dateFrom), r.price * DATEDIFF(b.dateFrom, b.dateTo) * -1 FROM Room r, Guest g, Booking b, Hotel h WHERE h.hotelNo = r.hotelNo AND r.roomNo = b.roomNo AND b.guestNo = g.guestNo AND CURDATE() = b.dateTo AND b.hotelNo = r.hotelNo;
